import {Component, OnInit} from '@angular/core';
import {Input} from "postcss";

@Component({
  selector: 'app-resume-profils',
  standalone: true,
  imports: [],
  templateUrl: './resume-profils.component.html',
  styleUrl: './resume-profils.component.scss'
})
export class ResumeProfilsComponent implements OnInit{
  @Input photoUrl: string = '';
  @Input authorName: string = '';
  @Input preferredTechnologies: string[] = [];

  ngOnInit(): void {
  }
}


<div class="flex items-center bg-gray-800 text-gray-300 p-4 rounded-lg">
  <!-- Photo de profil -->
  <img [src]="photoUrl" alt="Photo de {{ authorName }}" class="h-16 w-16 rounded-full mr-4">

  <!-- Informations de l'auteur -->
  <div>
    <h2 class="text-lg font-bold">{{ authorName }}</h2>

    <!-- Technologies préférées -->
    <div class="flex flex-wrap items-center mt-2">
      <span *ngFor="let tech of preferredTechnologies" class="bg-cyan-700 text-cyan-100 rounded-full px-3 py-1 text-xs font-medium mr-2 mt-2">
        {{ tech }}
      </span>
    </div>
  </div>

  <div class="mt-6 flex space-x-3 md:mt-0 md:ml-4">
    <!--    <button type="button" class="inline-flex items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-cyan-500">    rapport</button>-->
    <button type="button" class="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-cyan-600 hover:bg-cyan-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-cyan-500"> Partager cet article</button>
  </div>
</div>
