import {Component, OnInit} from '@angular/core';
import { Chart } from 'chart.js/auto';


@Component({
  selector: 'app-affectation-praticien',
  standalone: true,
  imports: [],
  templateUrl: './affectation-praticien.component.html',
  styleUrl: './affectation-praticien.component.scss'
})
export class AffectationPraticienComponent implements OnInit{
  financialImpactRadarChart!: any;
  dutyHoursPieChart!: any;


  constructor() {
  }

  ngOnInit(): void {
    this.createRadarChart();
    this.createPieChart();
  }
  private createRadarChart(): void {
    this.financialImpactRadarChart = new Chart('financialImpactRadarChart', {
      type: 'radar',
      data: {
        labels: ['CCAM', 'NGAP', 'Labo'], // Types d'actes médicaux
        datasets: [
          {
            label: 'Contribution financière 2023',
            data: [200000, 150000, 100000], // Données fictives pour 2023
            backgroundColor: 'rgba(54, 162, 235, 0.2)', // Couleur de remplissage
            borderColor: 'rgba(54, 162, 235, 1)', // Bordure bleue pour 2023
            borderWidth: 2,
            pointBackgroundColor: 'rgba(54, 162, 235, 1)', // Points en bleu
          },
          {
            label: 'Contribution financière 2024',
            data: [210000, 160000, 130000], // Données fictives pour 2024
            backgroundColor: 'rgba(255, 99, 132, 0.2)', // Couleur de remplissage
            borderColor: 'rgba(255, 99, 132, 1)', // Bordure rouge pour 2024
            borderWidth: 2,
            pointBackgroundColor: 'rgba(255, 99, 132, 1)', // Points en rouge
          }
        ]
      },
      options: {
        responsive: true,
        plugins: {
          tooltip: {
            callbacks: {
              label: function(context) {
                let label = context.dataset.label || '';
                if (label) {
                  label += ': ';
                }
                label += `${context.parsed.r}€`; // Afficher les montants en euros
                return label;
              }
            }
          },
          title: {
            display: true,
            text: '[DFAC] Comparaison des contributions financières (CCAM, NGAP, Labo)'
          }
        },
        scales: {
          r: {
            beginAtZero: true,
            pointLabels: {
              font: {
                size: 14
              }
            }
          }
        }
      }
    });
  }

  private createPieChart(): void {
    this.dutyHoursPieChart = new Chart('dutyHoursPieChart', {
      type: 'pie',
      data: {
        labels: ['Heures d\'enseignement', 'Garde de nuit', 'Astreintes'], // Catégories
        datasets: [
          {
            label: 'Répartition des heures',
            data: [40, 30, 20], // Données fictives pour la répartition
            backgroundColor: [
              'rgba(75, 192, 192, 0.7)', // Couleur pour enseignement
              'rgba(255, 159, 64, 0.7)', // Couleur pour garde de nuit
              'rgba(153, 102, 255, 0.7)' // Couleur pour astreintes
            ],
            hoverOffset: 4
          }
        ]
      },
      options: {
        responsive: true,
        plugins: {
          tooltip: {
            callbacks: {
              label: function(context) {
                let label = context.label || '';
                if (label) {
                  label += ': ';
                }
                label += `${context.parsed}%`; // Affiche en pourcentage
                return label;
              }
            }
          },
          title: {
            display: true,
            text: '[CME] Répartition des heures d\'enseignement, gardes et astreintes'
          }
        }
      }
    });
  }


}
<div class="p-4 bg-gray-50">
  <!-- Partie gauche : Informations sur le praticien -->
  <div class="bg-white p-4 rounded-lg shadow-md w-full md:w-1/3 mb-4">
    <h2 class="text-lg font-bold text-gray-700">OLIVIER MOREL</h2>
    <p class="text-gray-600"><strong>Grade : </strong>PU/PH</p>
    <p class="text-gray-600"><strong>ETP statutaire : </strong>1</p>
    <p class="text-gray-600"><strong>UF d'affectation : </strong>6261 - CPDP</p>
  </div>

  <p class="text-xl font-semibold text-center text-gray-800 mb-6">Visualisation CCAM - Dr Morel Olivier</p>
  <div class="flex flex-col space-y-6 p-6 bg-gray-50">
    <!-- Graphique CCAM en courbe (par mois) -->
    <div class="w-full h-96 flex justify-center items-center bg-gray-100 rounded-lg shadow-lg">
      <canvas id="financialImpactRadarChart"></canvas>
    </div>

    <!-- Histogramme CCAM (par jours de la semaine) -->
    <div class="w-full h-96 flex justify-center items-center bg-white rounded-lg shadow-lg">
      <canvas id="dutyHoursPieChart"></canvas>
    </div>
  </div>


</div>
